import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import userService from './services/user'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [user, setUser] = useState(null)
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  const handleLogin = async (event) => {
    event.preventDefault()
    const credentials = {username, password}
    console.log('trying to log in', credentials);
    try {
      const user = await userService.signIn(credentials)
      console.log(user);
      setUser(user)
      setUsername('')
      setPassword('')

      console.log(user);

      window.localStorage.setItem(
        'user', JSON.stringify(user)
      )
      blogService.setToken(user.token)
    }
    catch {

    }
  }

  const LoginForm = () => {
    return (
      <form onSubmit={handleLogin}>
        <div>
          <label style={{display: "block"}} for="uname"><b>Username</b></label>
          <input type="text" value={username} placeholder="Enter Username" name="uname" onChange={({target}) => setUsername(target.value)} required/>
          <label style={{display: "block"}} for="psw"><b>Password</b></label>
          <input type="password" value={password} placeholder="Enter Password" name="psw" onChange={({target}) => setPassword(target.value)} required/>
          <button style={{display: "block"}} type='submit'>submit</button>
        </div>
    </form>
    )
  }

  const BlogsList = () => {
    return (
      <div>
        <h2>blogs</h2>
        {
        blogs.map(blog =>
          <Blog key={blog.id} blog={blog} />
        )
        }
      </div>
    )
  }

  useEffect( async () => {
    let user = window.localStorage.getItem('user')
    {user && setUser(user)}
    const blogs = await blogService.getAll()
    console.log(blogs);
    setBlogs(blogs)
  }, [])

  return (
    <div>
      {
        user ? BlogsList() : LoginForm()
      }
    </div>
  )
}

export default App
