import React, {useRef} from 'react'
import Togglable from './Togglable'
import blogsService from '../services/blogs'

const Blog = ({blog, updateBlog}) => {
  const blogRef = useRef()

  const likeBlog = async (id) => {
    console.log('likeblog called');
    const response = await blogsService.patchBlog(id)
    console.log('liked blog:', response)
    updateBlog('likes', blog.likes + 1)
  }

  return (
  <div key={Math.random()}>
    {blog.title} {blog.author}
    <Togglable ref={blogRef} openButtonLabel='info' closeButtonLabel='hide'>
      <div>
        <p>id: {blog.id}</p>
        <p>url: {blog.url}</p>
        <p style={{display: 'inline-block', marginRight: '5px'}}>likes: {blog.likes}</p><button onClick={() => likeBlog(blog.id)}>like</button>
      </div>
    </Togglable>
  </div>  
)}

export default Blog
