import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import userService from './services/user'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [user, setUser] = useState(null)
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [title, setTitle] = useState()
  const [url, setUrl] = useState()

  const handleLogin = async (event) => {
    event.preventDefault()
    const credentials = {username, password}
    console.log('trying to log in', credentials);
    try {
      const user = await userService.signIn(credentials)
      console.log(user);
      setUser(user)
      setUsername('')
      setPassword('')

      console.log(user);

      window.localStorage.setItem(
        'user', JSON.stringify(user)
      )
      blogService.setToken(user.token)
    }
    catch {

    }
  }

  const handleLogout = () => {
    localStorage.clear()
    setUser(null)
    userService.setToken(null)
  }

  const handleNewBlog = (e) => {
    e.preventDefault()
    console.log('posting blog:', {title: title, url: url});
    blogService.postNewBlog()
  }



  const LoginForm = () => {
    return (
      <form onSubmit={handleLogin}>
        <div>
          <label style={{display: "block"}}><b>Username</b></label>
          <input type="text" value={username} placeholder="Enter Username" name="uname" onChange={({target}) => setUsername(target.value)} required/>
          <label style={{display: "block"}}><b>Password</b></label>
          <input type="password" value={password} placeholder="Enter Password" name="psw" onChange={({target}) => setPassword(target.value)} required/>
          <button style={{display: "block"}} type='submit'>submit</button>
        </div>
    </form>
    )
  }

  const BlogsList = () => {
    return (
      <div>
        <h2>blogs</h2>
        {
        blogs.map(blog =>
          <Blog key={blog.id} blog={blog} />
        )
        }
      </div>
    )
  }

  const UserInfo = () => {
    return (
      <div>
        <p>{user.username} logged in</p>
        <button onClick={handleLogout}>logout</button>
      </div>
    )
  }

  const CreateNewBlogWindow = () => {
    return (
        <form onSubmit={handleNewBlog}>
          <div>
            <label>title:</label>
            <input type='text' value={title} onChange={({target}) => setTitle(target.value)}></input>
          </div>
          <div>
            <label>url:</label>
            <input type='text' value={url} onChange={({target}) => setUrl(target.value)}></input>
          </div>
          <button type='submit'>submit</button>
        </form>
    )
  }

  const LoggedInView = () => {
    return (
      <>
        <UserInfo></UserInfo>
        <BlogsList></BlogsList>
        <CreateNewBlogWindow></CreateNewBlogWindow>
      </>
    )
  }



  useEffect(() => {
    let user = window.localStorage.getItem('user')
    {user && setUser(user)}
    //Async stuff
    async function doStuff() {
      const blogs = await blogService.getAll()
      console.log(blogs);
      setBlogs(blogs)
    }
    doStuff()
  }, [])

  return (
    <div>
      {
        user ? LoggedInView() : LoginForm()
      }
    </div>
  )
}

export default App
