import Togglable from './Togglable'
const Blog = ({blog}) => {
  const blogRef = useRef()

  return (
  <div key={Math.random()}>
    {blog.title} {blog.author}
    <Togglable ref={blogRef} buttonLabel='info'>
      <div>
        <p>id: {blog.id}</p>
        {/* <p>url: {blog.url}</p>
        <p>likes: {blog.likes}</p>
        <p>user: {blog.user}</p> */}
        <p>it be working</p>
      </div>
      <button onClick={blogRef.current.toggleVisibility()}>show info</button>
    </Togglable>
  </div>  
)}

export default Blog
