import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import userService from './services/user'

const BlogsList = ({blogs}) => {
  return (
    <div>
      <h2>blogs</h2>
      {
      blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )
      }
    </div>
  )
}

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [user, setUser] = useState(null)
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')

  const handleLogin = (event, credentials) => {
    event.preventDefault()
    userService.signIn({username: username, password})
  }

  const LoginForm = ({handleLogin}) => {
    return (
      <form onSubmit={() => handleLogin()}>
        <div>
          <label style={{display: "block"}} for="uname"><b>Username</b></label>
          <input type="text" placeholder="Enter Username" name="uname" onChange={target => setUsername(target.value)} required/>
          <label style={{display: "block"}} for="psw"><b>Password</b></label>
          <input type="password" placeholder="Enter Password" name="psw" onChange={target => setPassword(target.value)} required/>
          <button style={{display: "block"}} type='submit'>submit</button>
        </div>
    </form>
    )
  }

  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs( blogs )
    )  
  }, [])

  return (
    <div>
      {
        user ? BlogsList(blogs) : LoginForm(handleLogin)
      }
    </div>
  )
}

export default App
